            <i
              className="fa fa-bars"
              style={{ color: "#AAAAAA", right: "1%" }}
            ></i>
          </Link>
        </nav>
        <div
          className="mobileNavbar"
          id="mobileNavbar"
          style={{ display: "none" }}
