                <form className="form-inline" style={{ padding: "14px 16px" }}>
                  <div className="input-group">
                    <input
                      className="form-control"
                      placeholder="Search meal or category"
                      style={{
                        backgroundColor: "#fd7e14",
                        border: "1px solid #FFFFFF",
                        width: "80%"
                      }}
                    />
                    <span className="input-group-append">
                      <button
                        className="btn btn-outline-secondary"
                        type="button"
                        style={{
                          backgroundColor: "#FFFFFF",
                          borderColor: "#fd7e14"
                        }}
                      >
                        <i
                          className="fa fa-search"
                          style={{ color: "#fd7e14" }}
                        ></i>
                      </button>
                    </span>
                  </div>
                </form>
              </li>
              <li className="nav-item" style={{ padding: "14px 16px" }}>
                <Link
                  to="/grocery"
                  className="nav-link px-2"
                  style={{ color: "#FFFFFF" }}
                >
                  Login / Register
                </Link>
              </li>
              <li className="nav-item" style={{ padding: "14px 16px" }}>
                <a
                  className="nav-link px-2"
                  href="#"
                  style={{ color: "#FFFFFF" }}
                >
                  Wishlist
                </a>
              </li>
              <li
                className="nav-item"
                style={{
                  padding: "14px 16px",
                  borderBottom: "1px solid #FFFFFF"
                }}
              >
                <Link
                  to="/products"
                  className="nav-link px-2"
                  style={{ color: "#FFFFFF" }}
                >
                  Shopping Cart
                </Link>
              </li>
              <li className="nav-item" style={{ padding: "14px 16px" }}>
                <Link
                  to="/v2"
                  className="nav-link px-2"
                  style={{ color: "#FFFFFF" }}
                >
                  {" "}
                  Receipes
                </Link>
              </li>
              <li className="nav-item" style={{ padding: "14px 16px" }}>
                <Link
                  to="/grocery"
                  className="nav-link px-2"
                  style={{ color: "#FFFFFF" }}
                >
                  Grocery List
                </Link>
              </li>
              <li className="nav-item" style={{ padding: "14px 16px" }}>
                <Link
                  to="/products"
                  className="nav-link px-2"
                  style={{ color: "#FFFFFF" }}
                >
                  Products
                </Link>
              </li>
              <li
                className="nav-item"
                style={{
                  padding: "14px 16px",
                  borderBottom: "1px solid #FFFFFF"
                }}
              >
                <a
                  className="nav-link px-2"
                  href="#"
                  style={{ color: "#FFFFFF" }}
                >
                  Stats
                </a>
              </li>
            </ul>
          </div>
        </div>
        <nav
          className="navbar navbar-expand-md  navbar-light navbar-second"
          style={{ backgroundColor: "#EEEEEE" }}
        >
          <div className="navbar-collapse collapse pt-2 pt-md-0" id="navbar2">
            <ul className="navbar-nav">
              <li className="nav-item active" style={{ marginRight: "50%" }}>
                <Link to="/v2" className="nav-link px-2">
                  Home
                </Link>
              </li>
              <li className="nav-item" style={{ marginRight: "50%" }}>
                <Link to="/products" className="nav-link px-2">
                  Products
                </Link>
              </li>
              <li className="nav-item" style={{ marginRight: "50%" }}>
                <Link to="/v2" className="nav-link px-2">
                  Receipes
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        {/* <div className="topnav" id="myTopnav2">
    <Link to="/">CC</Link>
    <Link to="/">Recipes</Link>

    <div className="dropdown">
        <button className="dropbtn"> 
            Shop <i className="fa fa-caret-down"></i>
        </button>
        <div className="dropdown-content">
            <Link to="/products">Food Products</Link>
            <Link to="/products">Kitchen Products</Link>
            <Link to="/products">Other Household Items</Link>
        </div>
    </div>

    <Link to="/v1">Stats</Link>
    <Link to="javascript:void(0);" className="icon" onClick={()=>{console.log("Comes thru here"); myFunction()}} >
    <i className="fa fa-bars" ></i>
    </Link>
</div> */}

        <Typeahead
          options={this.products}
          placeholder="Find Meals (and Ingredients) here.."
          id="typeahead"
          // onChange={(selected) => {
          //     // Handle selections...
          //   }}
          filterBy={["product_name"]}
        />

        {/* <Typeahead
                    onChange={this.handleClickTypeahead}
