          <Route
            path="/grocery"
            render={props => (
              // <RecipeContentSection selectedMeal= {this.state.selectedMeal}/>
              <div>
                <div>
                  <b>Your Grocery List</b>
                </div>
                <GroceryPage
                  auth={isAuthenticated}
