import React, { Component } from "react";
import { Button, Modal, Carousel } from "react-bootstrap";

import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./text_slider.css";

class TextSlider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalIsOpen: false,
      increment: 0,
      checked: false,
      base_index: 0,
      index: 0
    };
    this.handleSelect = this.handleSelect.bind(this);
    this.updateInstructionsDisplayBaseIndex = this.updateInstructionsDisplayBaseIndex.bind(
      this
    );
  }

  handleSelect(selectedIndex) {
    this.setState({ index: selectedIndex+1 });
  }

  updateInstructionsDisplayBaseIndex(event) {
    console.log(event.target.innerText);
    var button = event.target.innerText;
    var regExp = "/^w+[ ]/d  $/";
    var slide_index = button.match(regExp);
    //console.log(slide_index);
    var last_chars = button.slice(6, 7);

    var slide_num = Number(last_chars);

    this.setState({ base_index: slide_num * 3 });
    //var base_index = slide_num*3;
    //console.log("Updating base index on click to: " +this.state.base_index);
  }

  render() {
    const { mealPrep1, value } = this.props;
    console.log({ mealPrep1 }, "pppppppppp");
    var popUpSlides = [];
    var carouselSlides = [];
    var i;
    var newarray = [];
    const instructionsLength = value.instructions.length;

    for (i = 0; i < instructionsLength / 3; i++) {
      popUpSlides.push(
        <button key={i} onClick={this.updateInstructionsDisplayBaseIndex}>
          Slide {i}{" "}
        </button>
      );

      carouselSlides.push(
        <Carousel.Item>
              <img
                className="img-responsive imageHeighgt"
                src="https://www.beautycolorcode.com/f6f0f0-2880x1800.png"
                alt="First slide"
              />
              <Carousel.Caption>
              <div id="mealPrepChunk">

      {(i*3) + 0}. {mealPrep1[(i*3) + 0]}  
      {(i*3) + 1}. {mealPrep1[(i*3) + 1]}
      {(i*3) + 2}. {mealPrep1[(i*3) + 2]}
        </div>
              </Carousel.Caption>
            </Carousel.Item>
      )

    }

        for (i = 1; i < mealPrep1.length; i = i + 3) {
      if (mealPrep1[i] === undefined) {
        newarray.push([mealPrep1[i - 1], "", ""]);
      }
      if (mealPrep[i + 1] === undefined) {
        newarray.push([mealPrep1[i - 1], mealPrep1[i], ""]);
      } else {
        newarray.push([mealPrep1[i - 1], mealPrep1[i], mealPrep1[i + 1]]);
      }
    }
    // const mealPrep1 = newarray.map(step => <ol key={step}> {step} </ol>);

    return (
      <div>

        {popUpSlides}

        <div id="mealPrepChunk">
          {mealPrep1[this.state.base_index + 0]}
          {mealPrep1[this.state.base_index + 1]}
          {mealPrep1[this.state.base_index + 2]}
        </div>



         <Carousel activeIndex={this.state.index} onSelect={this.handleSelect}>
            {carouselSlides}
        </Carousel> 

{/* <Carousel activeIndex={this.state.index} onSelect={this.handleSelect}>
            <Carousel.Item>
              <img
                className="img-responsive imageHeighgt"
                src="https://www.beautycolorcode.com/f6f0f0-2880x1800.png"
                alt="First slide"
              />
              <Carousel.Caption>
              <div id="mealPrepChunk">
          {mealPrep1[this.state.index + 0]}
          {mealPrep1[this.state.index + 1]}
          {mealPrep1[this.state.index + 2]}
        </div>
              </Carousel.Caption>
            </Carousel.Item>
        </Carousel> */}


      </div>


    );
  }
}
export default TextSlider;
