import React from 'react';
import './style.css';
import PageTitle from '../CommonComponents/PageTitle'
import { Spinner } from 'react-bootstrap'
import { Container, Alert, Card, Col, Row, Form, Button, Modal } from 'react-bootstrap'
import { Link } from 'react-router-dom';
export default class GroceryPage extends React.Component {
  state = {
    dataTypeaheadState:'',
    newcustomerId: '',
    valueData: null,
    Authentication: false,
    customerId: null,
    email: '',
    password: '',
    messageErr: false,
    messageSuccess: false,
    messageSuccessCreate: false,
    showAlert: false,
    messageAlert: '',
    variant: '',
    idItem: '',
    deletedItemId: null,
    showCreate: false,
    idsItems: null,
    deletedItemsId: null,
    lasIdListState: null,
    valueProductName: '',
    valueProductImage: '',
    valueProductPrice: '',
    valueProductSize: '',
    valuePricePerOunce: '',
    errormsg: '',
  }


  handleChange = ({ target: { value, name } }) =>
    this.setState({ [name]: value });


  componentDidMount() {
    const { auth } = this.props;
    this.setState({ Authentication: auth })
    fetch('/api/grocery', {
      method: 'GET',
      headers: {
        'Content-type': 'application/json',
      },
    })
      .then(res => {

        return res.json()

      })
      .then(response => {
        if (response.success && response.data) {
          this.setState({ Authentication: true });
        } else {
          this.setState({ Authenticated: false })
        }
        this.setState({ customerId: response.data })
        const { customerId } = this.state;
        fetch(`/api/getList/${customerId}`, {
          method: 'GET',
          credentials: 'same-origin',
          headers: {
            'Content-Type': 'application/json',
          },

        })
          .then(res => {
            return res.json()
          })
          .then(response => {
            if (response) {//all lists for this customer
              console.log('response id ',response);

              this.setState({ valueData: response.data })
            }
          })
          .catch(() => {
            this.setState({
              messageAlert: 'Internal Server Error',
              showAlert: true,
              variant: 'danger'
            },
              () =>
                setTimeout(() => {
                  this.setState({ messageAlert: '', showAlert: false })
                }, 8000)
            )
          })

      })
    this.handleClick = () => {
      const { email, password } = this.state;
      if (email && password) {
        // make a requset to the back with method post and data{email , password}
        fetch('/api/login', {
          method: 'POST',
          credentials: 'include',
          headers: {
            'Content-type': 'application/json',
          },
          body: JSON.stringify({
            email,
            password,
          }),
        })
          .then(response => {
            if (response.status === 400 || response.status === 404) {
              this.setState({ messageErr: 'Bad Request , Check username or password ... !!' });
            } else if (response.status === 401) {
              this.setState({ messageErr: 'you are UnAuthorized' });
            } else if (response.status >= 500) {
              this.setState({ messageErr: 'Sorry , Internal Server ERROR' })
            } else {
              this.setState({ messageErr: '' });
              this.setState({ isAuthenticated: true })
              this.setState({ messageSuccess: 'login sucessfully ' });
              return window.location.href = '/grocery'
            }
          })
          .catch(() => {
            this.setState({
              messageAlert: 'Internal Server Error',
              showAlert: true,
              variant: 'danger'
            },
              () =>
                setTimeout(() => {
                  this.setState({ messageAlert: '', showAlert: false })
                }, 8000)
            )
          })
      } else {
        this.setState({ messageErr: 'Please enter all fields' });
      }
    };


    this.handleClose = e => {
      if (e) e.stopPropagation();
      this.setState({ showCreate: false });
    };
    this.handleShowDeleteItem = (idItem) => {
      this.setState({ deletedItemId: idItem });
      const { customerId, deletedItemId } = this.state;
      fetch(`/api/remove-item/${idItem}/${customerId}`, {
        method: 'DELETE',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },

      })
        .then(res => {
          return res.json()

        })
        .then(response => {
          console.log('remove item',response);

          this.setState({
            messageAlert: 'deleted successfully',
            showAlert: true,
            variant: 'success'
          },
            () =>
              setTimeout(() => {
                this.setState({ messageAlert: '', showAlert: false })
              }, 3500)
          )
          this.setState(prevState => {
            const newValueData = prevState.valueData.filter(
              item => item.id !== deletedItemId
            );
            return { valueData: newValueData };
          });

        })
        .catch(() => {
          this.setState({
            messageAlert: 'Internal Server Error',
            showAlert: true,
            variant: 'danger'
          },
            () =>
              setTimeout(() => {
                this.setState({ messageAlert: '', showAlert: false })
              }, 8000)
          )
        })
    }
    this.handleShowAddItem = (idItem) => {
      window.location.href = `cart-page/${idItem}`
    }

    this.handleShowDeleteList = (idsItems) => {
      const { customerId } = this.state;
      fetch(`/api/get-ids-items/${customerId}`, {
        method: 'GET',
        credentials: 'same-origin',
        headers: {
          'Content-Type': 'application/json',
        },

      })
        .then(res => res.json())
        .then(response => {
          if (response) {//all lists for this customer
            console.log('get-ids-items',response);

            let arrResItemDelete = response.data
            arrResItemDelete.map((resDelete) =>
              this.setState({ deletedItemsId: resDelete })
            )
          }

        })
        .catch(() => {
          this.setState({
            messageAlert: 'Internal Server Error',
            showAlert: true,
            variant: 'danger'
          },
            () =>
              setTimeout(() => {
                this.setState({ messageAlert: '', showAlert: false })
              }, 8000)
          )
        })

      fetch(`/api/remove-list/${customerId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },

      })
        .then(res => {
          return res.json()
        })
        .then(response => {
          console.log('remove list',response);

          this.setState({
            messageAlert: 'deleted successfully',
            showAlert: true,
            variant: 'success'
          },
            () =>
              setTimeout(() => {
                this.setState({ messageAlert: '', showAlert: false })
              }, 3500)
          )

          this.setState({ valueData: [] });
        })
        .catch(() => {
          this.setState({
            messageAlert: 'Internal Server Error',
            showAlert: true,
            variant: 'danger'
          },
            () =>
              setTimeout(() => {
                this.setState({ messageAlert: '', showAlert: false })
              }, 8000)
          )
        })
    }

    this.handleShowCreateList = () => {
      this.setState({ showCreate: true })
    }

    this.handleCreateList = () => {
      const { lasIdListState, valueProductName, valueProductImage, valueProductPrice, valuePricePerOunce, valueProductSize } = this.state;
      const { customerId } = this.state;
      const idItem = lasIdListState;
      fetch(`/api/create-list/${idItem}/${customerId}`, {
        method: 'POST',
        body: JSON.stringify({
          valueProductName,
          valueProductImage,
          valueProductPrice,
          valueProductSize,
          valuePricePerOunce,
        }),
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
      })
        .then(res => {
          return res.json();
        })
        .then(response => {
          console.log('create list',response);

          if (response) {
            const {valueData} = this.state;
            console.log('iddd',lasIdListState);
            this.setState({valueData:[...valueData, ...response.data], messageSuccessCreate: 'add successfull', errormsgImage: '', errormsg: '', valueProductName: '', valueProductImage: '', valueProductSize: '', valueProductPrice: '', valuePricePerOunce: '', lasIdListState: lasIdListState + 1 })

          }
          this.setState({messageSuccess:''})
        })

    }

    fetch('/api/get-ids-list', {
      method: 'GET',
      credentials: 'same-origin',
      headers: {
        'Content-Type': 'application/json',
      },

    })
      .then(res => {
        return res.json()
      })
      .then(response => {
        console.log('get-ids-list',response);

        let arrResponse = response.data;
        const lasIdList = arrResponse[arrResponse.length - 1]
        this.setState({ lasIdListState: lasIdList + 1 })

